import hashlib
import sqlite3


def hash_pass(data):
    if isinstance(data, str):
        data = data.encode()
    # Calculate SHA-256 hash
    sha256_hash = hashlib.sha256(data).hexdigest()

    return sha256_hash

# Convert data to bytes if itâ€™s not already


def create_db():
    database = "db.sqlite"

    connection = sqlite3.connect(database)
    c = connection.cursor()
    c.execute("""CREATE TABLE IF NOT EXISTS shop_items(name TEXT, image TEXT, price TEXT)""")
    c.execute("""
        CREATE TABLE IF NOT EXISTS employees(
            username TEXT,
            email TEXT,
            password TEXT,
            totp_secret TEXT
        )
    """)

    c.execute(
        'INSERT INTO shop_items VALUES("water", "data:image/jpeg;base64,", "100")')
    c.execute(
        'INSERT INTO shop_items VALUES("juice", "data:image/jpeg;base64,", "110")')
    c.execute(
        'INSERT INTO shop_items VALUES("candy", "data:image/jpeg;base64,", "10")')
    c.execute('INSERT INTO employees VALUES("itsjasonh", "signe4878@gmail.com", "{}")'.format(hash_pass("badword")))
    c.execute('INSERT INTO employees VALUES("theeguy9", "signe4878@gmail.com", "{}")'.format(hash_pass("badpassword")))
    c.execute('INSERT INTO employees VALUES("newguy29", "signe4878@gmail.com", "{}")'.format(hash_pass("pass123")))


    # Creating a table to store failed login attempts
    c.execute('''
    CREATE TABLE IF NOT EXISTS failed_logins (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        username TEXT NOT NULL,
        attempts INTEGER NOT NULL,
        last_attempt TEXT NOT NULL
    )
''')

    connection.commit()
    connection.close()


if __name__ == "__main__":
    create_db()
    print("Tables created successfully.")
